import { useSubmit, useLocation } from "@remix-run/react";
import { ChevronDownIcon, CheckIcon } from "@radix-ui/react-icons";
import { Button } from "~/components/ui/button";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "~/components/ui/dropdown-menu";
import { languages, languageValues, Language } from "~/data";

export interface LanguageMenuProps {
  value: Language;
}

export function LanguageMenu({ value }: LanguageMenuProps) {
  const submit = useSubmit();
  const location = useLocation();
  const selected = languages.find((l) => l.value === value) || languages[0];

  return (
    <DropdownMenu>
      <DropdownMenuTrigger asChild>
        <Button variant="ghost" className="p-0 md:pl-3 md:pr-3">
          <div className="w-6 h-6 mr-1">{icons[selected.value]}</div>
          <div className="hidden md:block">{selected.label}</div>
          <ChevronDownIcon className="md:ml-2 h-4 w-4" />
        </Button>
      </DropdownMenuTrigger>
      <DropdownMenuContent className="w-40">
        {languages.map((option) => (
          <DropdownMenuItem
            key={option.value}
            disabled={selected.value === option.value}
            onClick={() => {
              // Remove the language query parameter if when the session language changes.
              const searchParams = new URLSearchParams(location.search);
              searchParams.delete("language");

              submit(
                { language: option.value },
                {
                  method: "POST",
                  action: `${location.pathname}?${searchParams}`,
                }
              );
            }}
          >
            <div className="w-6 h-6 mr-1">{icons[option.value]}</div>
            <div className="flex-1">{option.label}</div>
            {selected.value === option.value && (
              <CheckIcon className="w-4 h-4" />
            )}
          </DropdownMenuItem>
        ))}
      </DropdownMenuContent>
    </DropdownMenu>
  );
}

